    function GetWeaponType($weapon_name) {
        global $weapon_types;
        global $gloves_models;

        if(isset($weapon_types[$weapon_name])) {
            return $weapon_types[$weapon_name];
        }elseif(in_array($weapon_name, $gloves_models)) {
            return 'gloves';
        }
        return 'knifes';
    }
